public class IncrementDecrement2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int a = 5 , b=10;
   System.out.println(a++ + b++ + a-- + b-- - a++ - b++);
	             //  (5   +   10)+(6  + 11) - (5 + 10);
                  //      (15    +  17      - 15)  = 17
	
	} 

} 

